// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import '../HooksFactory.sol';
import './TokenData.sol';

using HooksTemplateDataLib for HooksTemplateData global;
using HooksTemplateDataLib for FeeConfiguration global;

struct HooksTemplateData {
  address hooksTemplate;
  FeeConfiguration fees;
  bool exists;
  bool enabled;
  uint24 index;
  string name;
  uint256 totalMarkets;
}

struct FeeConfiguration {
  address feeRecipient;
  /// @dev Basis points paid on interest for markets deployed using hooks
  ///      based on this template
  uint16 protocolFeeBips;
  /// @dev Asset used to pay origination fee
  TokenMetadata originationFeeToken;
  /// @dev Amount of `originationFeeAsset` paid to deploy a new market using
  ///      an instance of this template.
  uint256 originationFeeAmount;
  /// @dev Balance of the borrower in `originationFeeAsset`
  uint256 borrowerOriginationFeeBalance;
  /// @dev Approval from the borrower for the hooks factory to transfer `originationFeeAsset`
  uint256 borrowerOriginationFeeApproval;
}

library HooksTemplateDataLib {
  function fill(
    HooksTemplateData memory data,
    HooksFactory factory,
    address hooksTemplate,
    address borrower
  ) internal view {
    HooksTemplate memory template = factory.getHooksTemplateDetails(hooksTemplate);
    data.hooksTemplate = hooksTemplate;
    data.exists = template.exists;
    data.enabled = template.enabled;
    data.index = template.index;
    data.name = template.name;
    data.totalMarkets = factory.getMarketsForHooksTemplateCount(hooksTemplate);
    data.fees.fill(template, factory, borrower);
  }

  function fill(
    FeeConfiguration memory data,
    HooksTemplate memory template,
    HooksFactory factory,
    address borrower
  ) internal view {
    data.feeRecipient = template.feeRecipient;
    data.protocolFeeBips = template.protocolFeeBips;
    data.originationFeeAmount = template.originationFeeAmount;
    if (template.originationFeeAsset != address(0)) {
      data.originationFeeToken.fill(template.originationFeeAsset);
      if (borrower != address(0)) {
        IERC20 feeAsset = IERC20(template.originationFeeAsset);
        data.borrowerOriginationFeeBalance = feeAsset.balanceOf(borrower);
        data.borrowerOriginationFeeApproval = feeAsset.allowance(borrower, address(factory));
      }
    }
  }
}

src/lens/LenderAccountData.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.20;

import '../WildcatArchController.sol';
import { OpenTermHooks } from '../access/OpenTermHooks.sol';
import './TokenData.sol';
import '../types/HooksConfig.sol';
import '../types/LenderStatus.sol';
import './HooksConfigData.sol';
import './HooksTemplateData.sol';
import './MarketData.sol';
import './RoleProviderData.sol';

using LenderAccountDataLib for LenderAccountData global;

struct LenderAccountData {
  address lender;
  uint256 scaledBalance;
  uint256 normalizedBalance;
  uint256 underlyingBalance;
  uint256 underlyingApproval;
  // Hooks data
  bool isBlockedFromDeposits;
  RoleProviderData lastProvider;
  bool canRefresh;
  uint32 lastApprovalTimestamp;
  bool isKnownLender;
}

interface IVersionedContract {
  function version() external view returns (string memory);
}

library LenderAccountDataLib {
  function fill(
    LenderAccountData memory data,
    WildcatMarket market,
    IERC20 underlying,
    OpenTermHooks hooks,
    address lenderAddress
  ) internal view {
    data.lender = lenderAddress;

    data.scaledBalance = market.scaledBalanceOf(lenderAddress);
    data.normalizedBalance = market.balanceOf(lenderAddress);

    data.underlyingBalance = underlying.balanceOf(lenderAddress);
    data.underlyingApproval = underlying.allowance(lenderAddress, address(market));
    if (address(hooks) != address(0)) {
      LenderStatus memory status = hooks.getLenderStatus(lenderAddress);
      if (status.lastProvider != address(0)) {
        data.isBlockedFromDeposits = status.isBlockedFromDeposits;
        data.lastProvider.fill(hooks.getRoleProvider(status.lastProvider));
        data.canRefresh = status.canRefresh;
        data.lastApprovalTimestamp = status.lastApprovalTimestamp;
      }
      data.isKnownLender = hooks.isKnownLenderOnMarket(lenderAddress, address(market));
    }
  }

  function fill(
    LenderAccountData memory data,
    WildcatMarket market,
    address lenderAddress
  ) internal view {
    IERC20 underlying = IERC20(market.asset());
    OpenTermHooks hooks = OpenTermHooks(market.hooks().hooksAddress());
    data.fill(market, underlying, hooks, lenderAddress);
  }

  function fill(
    LenderAccountData memory data,
    MarketData memory market,
    address lenderAddress
  ) internal view {
    data.fill(
      WildcatMarket(market.marketToken.token),
      IERC20(market.underlyingToken.token),
      OpenTermHooks(market.hooksConfig.hooksAddress),
      lenderAddress
    );
  }
}

src/lens/WithdrawalBatchData.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.20;

import '../WildcatArchController.sol';
import '../market/WildcatMarket.sol';
import '../types/HooksConfig.sol';
import '../access/MarketConstraintHooks.sol';
import './HooksConfigData.sol';
import './HooksInstanceData.sol';
import './HooksTemplateData.sol';
import './LenderAccountData.sol';
import './TokenData.sol';

using WithdrawalBatchDataLib for WithdrawalBatchData global;
using WithdrawalBatchDataLib for WithdrawalBatchLenderStatus global;
using WithdrawalBatchDataLib for WithdrawalBatchDataWithLenderStatus global;

enum BatchStatus {
  Pending,
  Expired,
  Unpaid,
  Complete
}

struct WithdrawalBatchData {
  uint32 expiry;
  BatchStatus status;
  uint256 scaledTotalAmount;
  uint256 scaledAmountBurned;
  uint256 normalizedAmountPaid;
  uint256 normalizedTotalAmount;
}

struct WithdrawalBatchLenderStatus {
  address lender;
  uint256 scaledAmount;
  uint256 normalizedAmountWithdrawn;
  uint256 normalizedAmountOwed;
  uint256 availableWithdrawalAmount;
}

struct WithdrawalBatchDataWithLenderStatus {
  WithdrawalBatchData batch;
  WithdrawalBatchLenderStatus lenderStatus;
}

library WithdrawalBatchDataLib {
  function fill(
    WithdrawalBatchData memory data,
    WildcatMarket market,
    uint32 expiry
  ) internal view {
    WithdrawalBatch memory batch = market.getWithdrawalBatch(expiry);
    data.expiry = expiry;
    data.scaledTotalAmount = batch.scaledTotalAmount;
    data.scaledAmountBurned = batch.scaledAmountBurned;
    data.normalizedAmountPaid = batch.normalizedAmountPaid;
    if (expiry >= block.timestamp) {
      data.status = BatchStatus.Pending;
    } else if (expiry > market.previousState().lastInterestAccruedTimestamp) {
      data.status = BatchStatus.Expired;
    } else {
      data.status = data.scaledAmountBurned == data.scaledTotalAmount
        ? BatchStatus.Complete
        : BatchStatus.Unpaid;
    }
    if (data.scaledAmountBurned != data.scaledTotalAmount) {
      uint256 scaledAmountOwed = data.scaledTotalAmount - data.scaledAmountBurned;
      uint256 normalizedAmountOwed = MathUtils.rayMul(scaledAmountOwed, market.scaleFactor());
      data.normalizedTotalAmount = data.normalizedAmountPaid + normalizedAmountOwed;
    } else {
      data.normalizedTotalAmount = data.normalizedAmountPaid;
    }
  }

  function fill(
    WithdrawalBatchLenderStatus memory data,
    WildcatMarket market,
    WithdrawalBatchData memory batch,
    address lender
  ) internal view {
    data.lender = lender;
    AccountWithdrawalStatus memory status = market.getAccountWithdrawalStatus(lender, batch.expiry);
    data.scaledAmount = status.scaledAmount;
    data.normalizedAmountWithdrawn = status.normalizedAmountWithdrawn;
    data.normalizedAmountOwed =
      MathUtils.mulDiv(batch.normalizedTotalAmount, data.scaledAmount, batch.scaledTotalAmount) -
      data.normalizedAmountWithdrawn;
    data.availableWithdrawalAmount =
      MathUtils.mulDiv(batch.normalizedAmountPaid, data.scaledAmount, batch.scaledTotalAmount) -
      data.normalizedAmountWithdrawn;
  }

  function fill(
    WithdrawalBatchDataWithLenderStatus memory data,
    WildcatMarket market,
    uint32 expiry,
    address lender
  ) internal view {
    data.batch.fill(market, expiry);
    data.lenderStatus.fill(market, data.batch, lender);
  }
}

